class Increment extends React.Component{
  constructor(props){
    super(props);
    this.state={n:props.aaa,timer:null}
  }

  componentDidMount(){
      this.play()
    }  


  increment=()=>{
    this.setState((state,props)=>({n: state.n + 1}))
  }

  play=()=>{
    this.setState({timer:setInterval(()=>{this.increment()
    },1000)})
  }
  stop=()=>{
    setInterval(this.state.timer)
    this.setState({timer:null})
  }
  render()
  {

    return(
      <div>
        <h1>it's {this.state.n}</h1>
        <button type="button" onClick={this.stop}></button>
      </div>
    )
  }
}

