class Increment extends React.Component{
  constructor(props){
    super(props);
    this.state={n:props.aaa}
  }
  shouldComponentUpdate()
  {
    return true
  }
  componentDidMount(){
      this.b=setInterval(()=>{this.play()
      },1000)
    }  


  play=()=>{
    this.setState((state,props)=>({n: state.n + 1}))
  }

  stop=()=>{
    clearInterval(this.b)  
  }
  render()
  {

    return(
      <div>
        <h1>it's {this.state.n}</h1>
        <button type="button" onClick={this.stop}></button>
      </div>
    )
  }
}

class Art extends React.Component{
  constructor(props){
    super(props);
    this.state={
      name:"antonio",
      firstname:'bony',
      old:'21',
      address:'Anosy isada'
    };
  }
  shouldComponentUpdate()
  {
    return true;
  }
