    if(this.state.name==="antonio")
    {
      this.setState({name:"bakary"})
    }
    else{
      this.setState({name:"antonio"})
    }
  }
  render(){
    return(
      <div>
        <p>I'm {this.state.firstname} {this.state.name} {this.state.old} years old and i'm live in fianaratsoa {this.state.address}</p>
        <button type="button" onClick={this.cha}></button>
        <Increment aaa={0}/>
      </div>
    )
  }
}
