import React from 'react';
import Button from '@mui/material/Button';
import  Field  from './Field';
import  Box  from '@mui/material/Box';
import {Link} from '@mui/material';
import { Typography } from '@mui/material';
import  Econ from './Econ.jpg';
export default  class Forms extends React.Component{
    constructor(props){
      super(props)
     this.state={
      usr:{
        username:'',
        border:'',
        borderRadius:'',
        borderColor:''
      },
      pwd:{
        password:'',
        border:'',
        borderRadius:'',
        borderColor:''
      }
     }
    }

    UpgradeState=(a,b)=>{
      if(this.state.a.b ==='')
      {
        this.setState({
          a:{
            border:1,
            borderColor:'error.main',
            borderRadius:'1'
          }
        })   
      } 
    }

    Handleclick=(event)=>{
      console.log(event.target.value);
      event.preventDefault();
     this.UpgradeState(usr,username);
     this.UpgradeState(pwd,password)
    }

    change=(event)=>{
      const name=event.target.name;
      const id= event.target.id;
      this.setState({
       [id]:{[name]:event.target.value} 
      })
    }

    render()
    {
      return(

        <Box
        sx={{
            margin:'auto',
            marginTop:5,
            width:'50ch',
            height:'55ch',
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            borderRadius:5,
            boxShadow:5,
            padding:4,}}
        >
          <Image/>
          <Typography  component="h1" variant="h2"
           sx={{
            fontFamily: "Helvetica",
            fontWeight:'bold',
            textShadow:15,
          }}>
           e-Delivery
           </Typography>
          <Typography component="h3" 
           sx={{
            margin:1,
          }}
          >
            Log into your account
          </Typography>
          <Box
          component="form" sx={{
            '& .MuiTextField-root': { m: 1, width: '40ch' },
           }}
          onSubmit={this.Handleclick}
          noValidate
          autoComplete="off"
          >
              <Field name='username' label='Username' type='text' value={this.state.usr.userame} onChange={this.change} id="usr" 
              border={this.state.usr.border} 
              borderColor={this.state.usr.borderColor} 
              borderRadius={this.state.usr.borderRadius}
              />
              <Field name='passsword' label='Password' type='password' value={this.state.pwd.password} onChange={this.change} id="pwd"
              border={this.state.pwd.border} 
              borderColor={this.state.pwd.borderColor} 
              borderRadius={this.state.pwd.borderRadius}
              />
              <Link href="#" variant="body2">
                  {"Forgot password"}
                </Link>
              <ButtonUsage click={this.Handleclick}/>
              <Link href="#" variant="body2">
                  {"Don't have an account? Sign Up"}
                </Link>
          </Box>
        </Box>
      )
    }
  }

  class ButtonUsage extends React.Component{
    constructor(props){
      super(props)
    }

    render()
    {
      return(
        <div>
          <Button variant="contained" color='secondary' fullWidth type='submit'>LOG IN</Button>
        </div> 
      )
    }
  }

  function Image(){
    return <img src={Econ} width="130px" height="130px"/>
  }


