class TS extends React.Component{
  constructor(props){
    super(props);
   this.state={
     nol:['antonio','b']
   }
  }
  change=(event)=>{
    this.setState({nol:Array.from(event.target.selectedOptions).map(o=>o.value)})
  }
  render(){
    return(
      <div>
        <select onChange={this.change} multiple>
          <option>1</option>
          <option>2</option>
          <option>3</option>
        </select>
      </div>
    )
  }
}


class Increment extends React.Component{
  constructor(props){
    super(props);
    this.state={n:props.aaa,on:0}
    this.timer=null
  }
  shouldComponentUpdate()
  {
    return true
  }
  componentDidMount(){
    this.play()
    }  

  componentWillUnmount(){
    clearInterval(this.timer)
  }  
  play=()=>{
    this.timer=setInterval(()=>{this.increment()
    },1000)
    this.setState({on:0})
  }
  increment=()=>{
    this.setState((state,props)=>({n: state.n + 1}))
  }
  stop=()=>{
    clearInterval(this.timer)
    this.timer=null
    this.setState({on:5})
  }
  render()
  {

    return(
      <div>
        <TS/>
        <h1>it's {this.state.n}</h1>
        {this.timer? <button  onClick={this.stop}>stop</button>:<button type="button" onClick={this.play}>play</button> }
      </div>
    )
  }
}

class Art extends React.Component{
  constructor(props){
    super(props);
    this.state={
      name:"antonio",
      firstname:'bony',
      old:'21',
      address:'Anosy isada'
    };
  }
  shouldComponentUpdate()
  {
    return true;
  }
  cha=()=>{
    if(this.state.name==="antonio")
    {
      this.setState({name:"bakary"})
    }
    else{
      this.setState({name:"antonio"})
    }
  }
  render(){
    return(
      <div>
        <p>I'm {this.state.firstname} {this.state.name} {this.state.old} years old and i'm live in fianaratsoa {this.state.address}</p>
        <button type="button" onClick={this.cha}></button>
        <Increment aaa={0}/>
      </div>
    )
  }
}
