class Art extends React.Component{
  constructor(props){
    super(props);
    this.state={
      name:"antonio",
      firstname:'bony',
      old:'21',
      address:'Anosy isada'
    };
  }
  shouldComponentUpdate()
  {
    return true;
  }
  cha=()=>{
    if(this.state.name==="antonio")
    {
      this.setState({name:"bakary"})
    }
    else{
      this.setState({name:"antonio"})
    }
  }
  render(){
    return(
      <div>
        <p>I'm {this.state.firstname} {this.state.name} {this.state.old} years old and i'm live in fianaratsoa {this.state.address}</p>
        <button type="button" onClick={this.cha}></button>
      </div>
    )
  }
}
class Football extends React.Component {
  shoot = (a, b) => {
    alert(b.type);
    /*
    'b' represents the React event that triggered the function,
    in this case the 'click' event
    */
  }
  render() {
    return (
      <button onClick={(ev) => this.shoot("Goal", ev)}>Take the shot!</button>
    );
  }
}

